mapper.select:
SELECT id
FROM tb_user
WHERE username = #{userName}
AND password = HEX(AES_ENCRYPT(#{password}, #{userName}))
LIMIT 1;

mapper.update:
UPDATE tb_user
    SET password = HEX(AES_ENCRYPT(#{password}, username))
WHERE id = #{userId}

mapper.select:
SELECT DISTINCT u.id, u.name, u.sex, u.tel, u.email, d.dept_name AS dept, u.hiredate, u.root, u.status, (SELECT GROUP_CONCAT(role_name separator 'ï¼Œ') FROM tb_role WHERE JSON_CONTAINS(u.role,CAST(id AS CHAR))) AS roles FROM tb_user u JOIN tb_role r ON JSON_CONTAINS(u.role,CONVERT(r.id,CHAR)) LEFT JOIN tb_dept d ON u.dept_id = d.id WHERE 1 = 1
AND u.name LIKE '%${name}%'
AND u.sex=#{sex}
AND r.role_name=#{role}
AND d.id=#{deptId}
AND u.status=#{status}
LIMIT #{start}, #{length}

SELECT
    COUNT(DISTINCT u.id)
FROM tb_user u
JOIN tb_role r ON JSON_CONTAINS(u.role,CONVERT(r.id,CHAR ))
WHERE 1=1

    AND u.name LIKE '%${name}%'


    AND u.sex=#{sex}


    AND r.role_name=#{role}


    AND u.dept_id=#{deptId}


    AND u.status=#{status}

mapper.delete:
DELETE FROM tb_user WHERE id IN
#{one}
AND root=FALSE
